mapper.sql:
    	FROM commonCode CC
		LEFT JOIN commonCodeGroup CCG on CC.commonCodeGroup_seq = CCG.seq
	    WHERE 1=1

				AND CC.delYn IS NOT NULL
				AND CC.delYn = #{shDelYn}



				AND CC.regDate BETWEEN #{shStartDate} AND #{shEndDate}
				AND CC.modDate BETWEEN #{shStartDate} AND #{shEndDate}


			AND CC.seq = #{shValue}
			AND CC.seqChar LIKE CONCAT ('%',#{shValue},'%')
			AND CC.codeName LIKE CONCAT ('%',#{shValue},'%')



			OR CC.seq = #{shValue2}
			OR CC.seqChar LIKE CONCAT ('%',#{shValue2},'%')
			OR CC.codeName LIKE CONCAT ('%',#{shValue2},'%')

mapper.insert:
INSERT INTO commonCode (
	commonCodeGroup_seq
	,seqChar
	,codeName
	,codeNameEng
	,useYn
	,regDate
	,delYn
) VALUES (
	#{CCcommonCodeGroup_seq}
	,#{CCseqChar}
	,#{CCcodeName}
	,#{CCcodeNameEng}
	,#{CCuseYn}
	,NOW()
	,0
)

SELECT last_insert_id()

mapper.update:
UPDATE commonCode CC
	SET
		CC.seqChar = #{CCseqChar}
		,CC.codeName = #{CCcodeName}
		,CC.codeNameEng = #{CCcodeNameEng}
		,CC.useYn = #{CCuseYn}
		,CC.delYn = = #{CCdelYn}
		,CC.commonCodeGroup_seq = #{CCGseq}
		,CC.modDate = NOW()
	WHERE 1=1
		AND CC.seq = #{CCseq}
