mapper.insert:
INSERT INTO commonCodeGroup (
	seqChar
	,groupName
	,groupNameEng
	,useYn
	,regDate
	,delYn
	,description
) VALUES (
	#{CCGseqChar}
	,#{CCGgroupName}
	,#{CCGgroupNameEng}
	,#{CCGuseYn}
	,NOW()
	,#{CCGdelYn}
	,#{CCGdescription}
)

SELECT last_insert_id()

mapper.update:
UPDATE commonCodeGroup CCG
	SET
		CCG.delYn = #{CCGdelYn}
		,CCG.useYn = #{CCGuseYn}
		,CCG.seqChar = #{CCGseqChar}
		,CCG.groupName = #{CCGgroupName}
		,CCG.groupNameEng = #{CCGgroupNameEng}
		,CCG.modDate = NOW()
		,CCG.description = #{CCGdescription}
WHERE 1=1
	AND CCG.seq = #{CCGseq}
