mapper.insert:
INSERT INTO member (
	id
	,password
	,name
	,gender
	,dob
	,mobile
	,email
	,regDate
	,MBdelYn
	,grade
) VALUES (
	#{MBid}
	,#{MBpassword}
	,#{MBname}
	,#{MBgender}
	,#{MBdob}
	,#{MBmobile}
	,#{MBemail}
	,NOW()
	,0
	,#{MBgrade}
)

SELECT last_insert_id()

INSERT INTO memberAddress (
	member_seq
	,addressName
	,addressZipcode
	,addressMain
	,addressDetail
	,addressMore
	,delYn
) VALUES (
	1
	,#{addressName}
	,#{addressZipcode}
	,#{addressMain}
	,#{addressDetail}
	,#{addressMore}
	,0
)

SELECT last_insert_id()

mapper.update:
UPDATE member MB
	SET
		id = #{MBid}
		,password = #{MBpassword}
		,name = #{MBname}
		,gender = #{MBgender}
		,dob = #{MBdob}
		,mobile = #{MBmobile}
		,email = #{MBemail}
		,delYn = #{MBdelYn}
		,grade = #{MBgrade}
WHERE 1=1
	AND MB.seq = #{MBseq}
