mapper.insert:
INSERT INTO product (
    productName
    ,price
    ,stock
    ,category
    ,uploadDate
    ,status
    ,delYn
) VALUES (
	#{PDproductName}
	,#{PDprice}
	,#{PDstock}
	,#{PDcategory}
	,NOW()
	,#{PDstatus}
	,0
)

SELECT last_insert_id()

mapper.update:
UPDATE product PD
	SET
		productName = #{PDproductName}
		,price = #{PDprice}
		,stock = #{PDstock}
		,category = #{PDcategory}
		,status = #{PDstatus}
		,delYn = #{PDdelYn}
	WHERE 1=1
		AND PD.seq = #{PDseq}

mapper.insert:
INSERT INTO ProductOption (
	optionMain
	,optionSub
	,productSeq
	,delYn
) VALUES (
	#{POoptionMain}
	,#{POoptionSub}
	,#{PDseq}
	,0
)

SELECT last_insert_id()

mapper.update:
UPDATE productOption
	SET
		optionMain = #{POoptionMain}
		,optionSub = #{POoptionSub}
		,productSeq = #{PDseq}
		,delYn = #{POdelYn}
	WHERE 1=1
		AND seq = #{POseq}
